import React, { useState, useEffect } from "react";
import { Box, InputLabel, TextField, Typography } from "@mui/material";
const labelStyle = { mb: 1, fontSize: "24px", fontWeight: "bold" };
const AddBlog = () => {
  const [formData, setformData] = useState({
    name: "",
    email: "",
    password: "",
  });
  return (
    <div>
      <form>
        <Box
          display={"flex"}
          flexDirection="column"
          width={"80%"}
          border={3}
          borderColor="#ccc"
          borderRadius={8}
          boxShadow="10px 10px 20px #ccc"
          padding={3}
          margin={"auto"}
          marginTop={4}
        >
          <Typography
            variant="h3"
            textAlign={"center"}
            fontStyle={"italic"}
            fontWeight={"bold"}
            marginBottom={2}
          >
            Post your Blog
          </Typography>
          <InputLabel value sx={labelStyle}>Title</InputLabel>
          <TextField margin="normal" label="Add title" variant="outlined" />
          <InputLabel sx={labelStyle}>Description</InputLabel>
          <TextField
            margin="normal"
            label="Add Description"
            variant="outlined"
          />
          <InputLabel sx={labelStyle}>ImageURL</InputLabel>
          <TextField margin="normal" label="Add ImageURL" variant="outlined" />
        </Box>
      </form>
    </div>
  );
};

export default AddBlog;
