import React, { useState } from "react";
import {
  AppBar,
  Button,
  Toolbar,
  Typography,
  Box,
  Tabs,
  Tab,
} from "@mui/material";

import {Link} from 're'

const Header = () => {
  const [value, setvalue] = useState(0);

  const navigation = useNavigation();
  return (
    <AppBar
      position="sticky"
      sx={{
        background:
          "linear-gradient(90deg, rgba(2,0,36,1) 0%, rgba(121,9,108,1) 17%, rgba(0,212,255,1) 100%)",
      }}
    >
      <Toolbar>
        <Typography variant="h5">BlogsApp</Typography>
        <Box marginLeft={"auto"} marginRight={"auto"}>
          <Tabs
            textColor="inherit"
            value={value}
            onChange={(e, val) => setvalue(val)}
          >
            <Tab LinkComponent={Link} label="All BLogs" />
            <Tab label="My BLogs" />
          </Tabs>
        </Box>
        <Box display="flex" marginLeft="auto">
          <Button
            variant="contained"
            color="warning"
            sx={{ margin: 1, borderRadius: 10 }}
          >
            Login
          </Button>
          <Button
            variant="contained"
            color="warning"
            sx={{ margin: 1, borderRadius: 10 }}
          >
            Signup
          </Button>
          <Button
            variant="contained"
            color="warning"
            sx={{ margin: 1, borderRadius: 10 }}
          >
            Logout
          </Button>
        </Box>
      </Toolbar>
    </AppBar>
  );
};

export default Header;
