  }
  return (
    <form>
      <Box
        display="flex"
        flexDirection={"column"}
        alignItems="center"
        justifyContent={"center"}
        boxShadow="10px 10px 20px #ccc"
        padding={3}
        margin="auto"
        marginTop={5}
        borderRadius={5}
        maxWidth={400}
      >
        <Typography
          padding={3}
          textAlign="center"
          variant="h4"
          fontWeight={600}
        >
          {isSignup ? "Register" : "Login"}
        </Typography>

        {isSignup && (
          <TextField
            value={formData.name}
            onChange={handleChange}
            type={"text"}
            variant="outlined"
            label="Name"
            margin="normal"
          />
        )}
        <TextField
          value={formData.email}
          onChange={handleChange}
          type={"email"}
          variant="outlined"
          label="Email"
          margin="normal"
        />
        <TextField
          value={formData.password}
          onChange={handleChange}
          type={"password"}
          variant="outlined"
          label="Password"
          margin="normal"
        />
        <Button
          sx={{ borderRadius: 3, marginTop: 2 }}
          color="warning"
          variant="contained"
        >
          Sumbit
        </Button>
        <Button
          onClick={() => setisSignup(!isSignup)}
          sx={{ borderRadius: 3, marginTop: 2, color: color }}
          variant="contained"
          textColor="white"
        >
          Change to {isSignup ? "Login" : "Signup"}
        </Button>
      </Box>
    </form>
  );
};

export default Auth;
